            listDeProduits.innerHTML+="<div id=\""+ ObjJSON[i].id +"\" class=\"product\">\n<a href=\"./product.html\?id="+ encodeURI(ObjJSON[i].id)+"\"title=\"En savoir plus...\">\n"
            +"<h2>"+ObjJSON[i].name+"</h2>\n"
