class Commande{
    constructor(prenom, nom, numero){
        this.prenom = prenom;
        this.nom = nom;
        this.numero = numero;
    }
}
var commande = new Commande("","", 0);
function updateCommande(){
    var prenom = document.getElementById("first-name").value;
    var nom = document.getElementById("last-name").value;;
    var numero = commande.numero + 1;
    commande = new Commande(prenom, nom, numero);
}

//Page confirmation
function updateCommandeText(){
    let prenom_element = document.getElementById("prenom");
    let nom_element = document.getElementById("nom");
    let numero_element = document.getElementById("numero");
    prenom_element.innerHTML = commande.prenom;
    nom_element.innerHTML = commande.nom;
    numero_element.innerHTML = commande.numero;	
}

//fonction qui vide le panier quand une commande est créée
function newCommand(){

    if ($('#form-order').valid()) {
        //Supprimer les items du localStorage
        var removeList = []; //Liste des keys a supprimer du localStorage
        for (var i = 0; i < window.localStorage.length; i++) {
            var key = window.localStorage.key(i);
            if (key != 'nbItems' && key != 'itemsInCart' && key != 'totalPrice'){
                removeList.push(key);
            }
        }

        for (var i=0; i < removeList.length; i++) {
            window.localStorage.removeItem(removeList[i]);
        }

        //remettre les variables a 0
        window.localStorage.setItem('itemsInCart', 'false');
        window.localStorage.setItem('nbItems', '0');
        window.localStorage.setItem('totalPrice', '0');

        //Changer l'affichage
        $('.count').hide();
        $(".shopping-cart-article" ).append( "<p>Aucun produit dans le panier.</p>" );
        $('#shopping-cart-items').hide();
    }
}
